import React, {useState} from "react";
import { HashLink } from "react-router-hash-link";
import 'animate.css';

const Home = () => {

    const [themeIcon, setThemeIcon] = useState("ðŸŒ™");

    function themeToggle() {
        // alert('Theme changed foo!')

        setThemeIcon (
            'â˜€'
        )
        let animateBg  = document.querySelector('.animateBg');



        animateBg.style.setProperty('--lightAnimate', 'linear-gradient(to bottom, skyblue, cornflowerblue)')
        // animateBg.classList.toggle('.animateBgDark::before');
    }




    return (
        <div id="home">

            <div className="animateBg">
                <div id="header">
                    <nav>
                        <ul className="headerLists">
                            <li className="themeToggle" onClick={themeToggle}>{themeIcon}</li>
                            <li className="headerLinks"><HashLink to='#home'> About</HashLink></li>
                            <li><HashLink to='#home'> Skills</HashLink></li>
                            <li><HashLink to='#home'> Projects</HashLink></li>
                            <li><HashLink to='#home'> Contact</HashLink></li>
                        </ul>
                    </nav>
                </div>
                <div id="startWrapper" className="" >
                    <div className="startText ">
                        <div >Hi, I'm Darwin | <span className="positionText">Front-End</span> <br/><span className="positionText2">Developer</span></div>
                        <div className="iconRow">
                            <a href="https://github.com/Meowinn" className="startIconLink"><img className="startIcons" src={require('../imgs/github.png')}></img></a>
                            <a href="https://www.linkedin.com/" className="startIconLink"><img className="startIcons"  src={require('../imgs/linkedin-logo.png')}></img></a>
                            <a href="mailto:darwinfagarang@gmail.com" className="startIconLink"><img className="startIcons" src={require('../imgs/gmail-logo.png')}></img></a>
                        </div>
                        <div className="aboutText">I love building and designing. I'm a life-long learner and I might have a thing for shawarma</div>
                        <button className="dlBtn" type="button">Download CV</button>
                    </div>

                </div>
            </div>


        <div className="cloudContainer">
            <div className="cloud one"></div>
            <div className="cloud two"></div>
            <div className="cloud three"></div>
            <div className="cloud four"></div>
            <div className="cloud five"></div>
        </div>








        </div>
    )
}

export default Home;
